import '../styles/Home.sass'



function ContactFormular(){


    const handleFormSubmit = async(event) => {
        event.preventDefault()
    
        const name = event.target.name.value
        const username = event.target.username.value
        const email = event.target.email.value
        const password = event.target.password.value
        const repeatPassword = event.target.repeatPassword.value
    
        if (password !== repeatPassword) {
            handleFeedback({ type: 'error', message: 'Password does not match'})
            return 
        }
    
        try {
            await registerUser(name, username, password, email)
            
            const token = await authenticateUser(username, password)
    
            sessionStorage.token = token
            props.onUserLoggedIn()
    
        } catch(error) {
            handleFeedback({ type: 'error', message: error.message})
        }
    }

    return <form className="Formular" onSubmit={handleFormSubmit}>
        <input className="Formular__imput" type="text" name="name" placeholder=" Name"/>
        <input className="Formular__imput" type="email" name="email" placeholder=" Email"/>
        <input className="Formular__imput" type="message" name="message" placeholder=" Your message"/>
        <button className="Formular__button">Send email</button>
    </form>
} 

export default ContactFormular
